CREATE TABLE IF NOT EXISTS `ctacte` (  `numero_cuenta` bigint(20) NOT NULL auto_increment,  `id_cliente` bigint(20) NOT NULL, `fecha_alta` datetime NOT NULL, `fecha_baja` datetime default NULL, `saldo` double(20,3) default NULL, `limite` decimal(20,3) default NULL, `suspendida` BOOLEAN NOT NULL DEFAULT 1,  PRIMARY KEY  (`numero_cuenta`), FOREIGN KEY (id_cliente) REFERENCES clientes( id ) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `item_ctacte` ( `id_op_ctacte` bigint(20) NOT NULL AUTO_INCREMENT, `fecha` datetime NOT NULL, `id_referencia` bigint(20) NOT NULL, `tipo_op` smallint(6) NOT NULL, `descripcion` tinytext COLLATE utf8_spanish_ci, `debe` decimal(20,3) DEFAULT NULL, `haber` decimal(20,3) DEFAULT NULL, `id_ctacte` bigint(20) NOT NULL, `numero_comprobante` tinytext COLLATE utf8_spanish_ci, PRIMARY KEY (`id_op_ctacte`), FOREIGN KEY (id_ctacte) REFERENCES ctacte(numero_cuenta) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;