CREATE TABLE IF NOT EXISTS `car_categorias` ( `id_categoria` int(1) NOT NULL auto_increment, `nombre` tinytext NOT NULL, `especial` tinyint(1) unsigned default NULL DEFAULT '0', PRIMARY KEY  (`id_categoria`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `car_establecimientos` ( `id_establecimiento` int(1) NOT NULL auto_increment, `nombre` tinytext NOT NULL, `respma` tinytext NOT NULL, PRIMARY KEY  (`id_establecimiento`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `car_caravana` ( `id_caravana` int(1) NOT NULL auto_increment, `codigo` char(50) NOT NULL, PRIMARY KEY  (`id_caravana`), UNIQUE KEY `codigorepetido` (`codigo`(1)) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `car_tri` ( `id_tri` int(1) NOT NULL auto_increment, `dta` tinytext NOT NULL, `razon` smallint(2) NOT NULL, `guia` tinytext NULL, `fecha` date NOT NULL, `id_categoria` int(2) default 0 NULL,  `id_estab_destino` int(2) NULL, `id_estab_origen` int(2) NULL, `id_comprador` int(2) default NULL, `id_vendedor` int(2) NULL, `cantidad_caravanas` int(2) default 0 NULL, PRIMARY KEY  (`id_tri`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `car_carv_tri` ( `id_caravana` int(1) NOT NULL, `id_tri` int(1) NOT NULL, PRIMARY KEY `id_caravana_tri` (`id_caravana`,`id_tri`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `car_carv_duenos` ( `id_caravana` int(1) NOT NULL, `id_cliente` int(1) NOT NULL,  `fecha_inicio` date NOT NULL, `fecha_fin` date NOT NULL, PRIMARY KEY  (`id_caravana`,`id_cliente`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;