CREATE TABLE IF NOT EXISTS `tipo_descuento` ( `id_tipo_descuento` bigint(20) NOT NULL AUTO_INCREMENT, `codigo` tinytext NULL, `nombre` tinytext NOT NULL, `cantidad` double(20,3) NOT NULL, PRIMARY KEY ( `id_tipo_descuento` ) ) DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `descuentos` ( `id_descuento` bigint(20) NOT NULL AUTO_INCREMENT, `texto` tinytext NOT NULL, `cantidad` double(20,3) NOT NULL, PRIMARY KEY ( `id_descuento` ) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `descuento_comprobante` (  `id_descuento` BIGINT(20) NOT NULL,  `id_comprobante` BIGINT(20) NOT NULL,  `tipo` int NOT NULL,  PRIMARY KEY ( `id_descuento`, `id_comprobante`, `tipo` ),  FOREIGN KEY `id_descuento` REFERENCES `descuentos`(`id_descuento`) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;