CREATE TABLE IF NOT EXISTS `garantias` ( `id_garantia` INT NOT NULL, `fecha_inicio` VARCHAR(45) NULL , `fecha_fin` VARCHAR(45) NULL , `id_cliente` BIGINT(10) NOT NULL , `id_equipamiento` BIGINT(20) NOT NULL , `id_factura` BIGINT(20) NOT NULL , `codigo_garantia` VARCHAR(45) NULL , PRIMARY KEY (`id_garantia`), INDEX `fk_garantias_clientes1_idx` (`id_cliente` ASC ), INDEX `fk_garantias_equipamiento1_idx` (`id_equipamiento` ASC) , INDEX `fk_garantias_factura1_idx` (`id_factura` ASC) , CONSTRAINT `fk_garantias_clientes1`  FOREIGN KEY (`id_cliente` ) REFERENCES `clientes` ( `id` ) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT `fk_garantias_equipamiento1` FOREIGN KEY (`id_equipamiento` ) REFERENCES `equipamiento` (`id_equipamiento` ) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT `fk_garantias_factura1` FOREIGN KEY (`id_factura` )   REFERENCES `factura` (`id_factura` ) ON DELETE NO ACTION ON UPDATE NO ACTION ) ENGINE = InnoDB;