CREATE TABLE IF NOT EXISTS `categoria_gastos` (  `id_categoria_gastos` bigint(20) NOT NULL auto_increment, `nombre` tinytext NOT NULL, `descripcion` text default NULL, PRIMARY KEY  (`id_categoria_gastos`), UNIQUE KEY `nombre_unico` ( nombre( 100 ) ) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `gastos` ( `id_gasto` bigint(20) NOT NULL AUTO_INCREMENT ,  `id_categoria` bigint(20) NOT NULL REFERENCES categoria_gastos( id_categoria_gastos ) ON DELETE RESTRICT,  `descripcion` tinytext NOT NULL,  `costo` decimal(20,3) NOT NULL DEFAULT 0.0, `fecha` date NOT NULL, `id_caja` bigint(20) DEFAULT NULL REFERENCES movimiento_caja( id_movimiento ),  PRIMARY KEY ( `id_gasto` ) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;