CREATE TABLE IF NOT EXISTS `autos` ( `id_auto` int(1) NOT NULL auto_increment, `patente` varchar(10) NOT NULL, `id_dueno` int(1) REFERENCES `clientes`(`id_cliente`), `marca` text NOT NULL, `modelo` text NOT NULL, `ano` year(4) default NULL, `color` tinytext default '', PRIMARY KEY  (`id_auto`) );
CREATE TABLE IF NOT EXISTS `presupuesto` ( `id_presupuesto` int(1) NOT NULL auto_increment, `fecha` datetime NOT NULL, `kilometraje` int(1) NOT NULL, `id_auto` int(1) NOT NULL REFERENCES `autos`(`id_auto`), `total` decimal(5,2) NOT NULL, `titulo` tinytext, `memo` tinytext, `contenido` longblob, `modificado` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP,  PRIMARY KEY  (`id_presupuesto`) );