CREATE TABLE IF NOT EXISTS `proveedor` ( `id` int(1) NOT NULL AUTO_INCREMENT, `nombre` tinytext CHARACTER SET latin1 NOT NULL, `direccion` blob, `email` tinytext CHARACTER SET latin1, `fax` tinytext CHARACTER SET latin1, `telefono_celular` tinytext CHARACTER SET latin1, `telefono_linea` tinytext CHARACTER SET latin1, `cuit_cuil` tinytext CHARACTER SET latin1, PRIMARY KEY (`id`), UNIQUE KEY `razon_social_unica` (`nombre`(100)), UNIQUE KEY `cuil_cuil_unico` (`cuit_cuil`(12)) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;