CREATE TABLE IF NOT EXISTS factura ( "id_factura" INTEGER PRIMARY KEY autoincrement, "id_cliente" INTEGER NOT NULL, "fecha" TEXT NOT NULL, "id_forma_pago" INTEGER NOT NULL, "serie" INTEGER NOT NULL, "numero" INTEGER NOT NULL, "total" NUMERIC NOT NULL, "id_ctate" NUMERIC NULL, "id_mov_caja" NUMERIC NULL, "anulada" NUMERIC NULL, "razon" TEXT NULL, "fecha_cancelada" TEXT NULL, "observaciones" TEXT DEFAULT NULL );