CREATE TABLE IF NOT EXISTS "main"."recibos" ("id_recibo" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "id_cliente" INTEGER NULL, "fecha_pago" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "texto" TEXT, "precio" FLOAT NOT NULL  DEFAULT 0, "forma_pago" INTEGER NOT NULL DEFAULT -1, "cancelado" BOOL NOT NULL  DEFAULT 0, "pagado" BOOL NOT NULL  DEFAULT 1, "serie" INTEGER NOT NULL, "numero" INTEGER NOT NULL, "id_caja" INTEGER DEFAULT NULL, "razon_cancelacion" TEXT DEFAULT NULL, "fechahora_cancelacion" DATETIME DEFAULT NULL );
INSERT OR IGNORE INTO "sqlite_sequence" ( "name", "seq" ) VALUES ( 'recibos', 1 );